import React, { useState } from "react";
import '../styles/'
const GameForm = () => {
 const [gameData, setGameData] = useState({
		name: "",
		url: "",
		author: "",
		published_date: "",
 });

 const handleInputChange = (e) => {
		const { name, value } = e.target;
		setGameData({
			...gameData,
			[name]: value,
		});
 };

 const handleCreate = (e) => {
		e.preventDefault();

		// Validate the form here if needed

		// Save to local storage
		const games = JSON.parse(localStorage.getItem("games")) || [];
		games.push(gameData);
		localStorage.setItem("games", JSON.stringify(games));

		// Clear form after submission
		setGameData({
			name: "",
			url: "",
			author: "",
			published_date: "",
		});
 };
	return (
		<div className="game-form-container">
			<h2>Create Game</h2>
			<form onSubmit={handleCreate}>
				<div>
					<label>
						Name:
						<input
							type="text"
							name="name"
							value={gameData.name}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<div>
					<label>
						URL:
						<input
							type="text"
							name="url"
							value={gameData.url}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<div>
					<label>
						Author:
						<input
							type="text"
							name="author"
							value={gameData.author}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<div>
					<label>
						Published Date:
						<input
							type="text"
							name="published_date"
							value={gameData.published_date}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<button type="submit">Create</button>
			</form>
		</div>
	);
};

export default GameForm;
