import React, { useState } from "react";
import GameList from "./GameList";

const GameForm = () => {
	const [gameData, setGameData] = useState({
		name: "",
		url: "",
		author: "",
		published_date: "",
	});

	const [games, setGames] = useState([]); // Added games state

	const [searchTerm, setSearchTerm] = useState("");

	const handleSearchChange = (e) => {
		setSearchTerm(e.target.value);
	};

	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setGameData({
			...gameData,
			[name]: value,
		});
	};

	const handleCreate = (e) => {
		e.preventDefault();

		// Validate the form here if needed

		// Save to local storage
		const updatedGames = [...games, gameData]; // Copy current games and add new game
		localStorage.setItem("games", JSON.stringify(updatedGames));
		setGames(updatedGames);

		// Clear form after submission
		setGameData({
			name: "",
			url: "",
			author: "",
			published_date: "",
		});
	};

	const handleEdit = (game) => {
		setGameData(game);
	};

	const handleDelete = (game) => {
		const updatedGames = games.filter((g) => g !== game);
		localStorage.setItem("games", JSON.stringify(updatedGames));
		setGames(updatedGames);
	};

	return (
		<div>
			<h2>Create Game</h2>
			<form onSubmit={handleCreate}>
				<div>
					<label>
						Name:
						<input
							type="text"
							name="name"
							value={gameData.name}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<div>
					<label>
						URL:
						<input
							type="text"
							name="url"
							value={gameData.url}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<div>
					<label>
						Author:
						<input
							type="text"
							name="author"
							value={gameData.author}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<div>
					<label>
						Published Date:
						<input
							type="text"
							name="published_date"
							value={gameData.published_date}
							onChange={handleInputChange}
						/>
					</label>
				</div>
				<button type="submit">Create</button>
			</form>
			<h2>Game List</h2>
			<div>
				<input
					type="text"
					value={searchTerm}
					onChange={handleSearchChange}
					placeholder="Search for a game..."
				/>
			</div>
			<GameList
				games={games}
				editGame={handleEdit}
				deleteGame={handleDelete}
				searchTerm={searchTerm}
			/>
		</div>
	);
};

export default GameForm;
