import React, { useState } from "react";
import "../styles/Login.css";

const GameForm = () => {
  const [gameData, setGameData] = useState({
    name: "",
    url: "",
    author: "",
    published_date: "",
  });

  const [errors, setErrors] = useState({
    name: "",
    url: "",
    author: "",
    published_date: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setGameData({
      ...gameData,
      [name]: value,
    });

    // Reset the error message when user starts typing
    setErrors({
      ...errors,
      [name]: "",
    });
  };

  const handleCreate = (e) => {
    e.preventDefault();

    // Validate the form here
    const newErrors = {};

    if (!gameData.name) {
      newErrors.name = "Name is required";
    }

    if (!gameData.url) {
      newErrors.url = "URL is required";
    }

    if (!gameData.author) {
      newErrors.author = "Author is required";
    }

    if (!gameData.published_date) {
      newErrors.published_date = "Published Date is required";
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    // Save to local storage
    const games = JSON.parse(localStorage.getItem("games")) || [];
    games.push(gameData);
    localStorage.setItem("games", JSON.stringify(games));

    // Clear form after submission
    setGameData({
      name: "",
      url: "",
      author: "",
      published_date: "",
    });
  };

  return (
    <div className="game-form-container">
      <h2>Create Game</h2>
      <form onSubmit={handleCreate}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={gameData.name}
            onChange={handleInputChange}
            required
          />
          <div className="error">{errors.name}</div>
        </div>
        <div className="form-group">
          <label>URL:</label>
          <input
            type="text"
            name="url"
            value={gameData.url}
            onChange={handleInputChange}
            required
          />
          <div className="error">{errors.url}</div>
        </div>
        <div className="form-group">
          <label>Author:</label>
          <input
            type="text"
            name="author"
            value={gameData.author}
            onChange={handleInputChange}
            required
          />
          <div className="error">{errors.author}</div>
        </div>
        <div className="form-group">
          <label>Published Date:</label>
          <input
            type="text"
            name="published_date"
            value={gameData.published_date}
            onChange={handleInputChange}
            required
          />
          <div className="error">{errors.published_date}</div>
        </div>
        <button type="submit">Create</button>
      </form>
    </div>
  );
};

export default Login;
