import React, { useState } from "react";
import { Table, Button, Space } from "antd";
import "../styles/GameList.css";

const GameList = () => {
    const [data, setData] = useState(getGameData());

		function getGameData() {
			const games = JSON.parse(localStorage.getItem("games")) || [];
			return games.map((game, index) => ({
				key: index,
				name: game.name,
				url: game.url,
				author: game.author,
				published_date: game.published_date,
			}));
		}
	const handleEdit = (record) => {
		console.log("Edit clicked for game:", record);
	};

	const handleDelete = (record) => {
		console.log("Delete clicked for game:", record);
		const updatedGames = data.filter((game) => game.key !== record.key);
		localStorage.setItem("games", JSON.stringify(updatedGames));
		setData(updatedGames);
	};

	const columns = [
		{
			title: "Name",
			dataIndex: "name",
			key: "name",
		},
		{
			title: "URL",
			dataIndex: "url",
			key: "url",
		},
		{
			title: "Author",
			dataIndex: "author",
			key: "author",
		},
		{
			title: "Published Date",
			dataIndex: "published_date",
			key: "published_date",
		},
		{
			title: "Action",
			key: "action",
			render: (text, record) => (
				<Space size="middle">
					<Button
						type="primary"
						onClick={() => handleEdit(record)}>
						Edit
					</Button>
					<Button
						type="danger"
						onClick={() => handleDelete(record)}>
						Delete
					</Button>
				</Space>
			),
		},
	];
	return (
		<div>
			<h2>List of Games</h2>
			<Table
				columns={columns}
				dataSource={data}
			/>
		</div>
	);
};

export default GameList;
